import React, { useState, useEffect } from 'react';
import apiService from '../services/apiService';
import { useAuth } from '../contexts/AuthContext';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    completedOrders: 0,
    totalCustomers: 0
  });
  const [recentOrders, setRecentOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    const loadDashboardData = async () => {
      try {
        // Simular carga de datos del dashboard
        // En una implementación real, harías llamadas a la API
        setStats({
          totalOrders: 156,
          pendingOrders: 23,
          completedOrders: 133,
          totalCustomers: 45
        });
        
        setRecentOrders([
          { id: 'ORD-001', customer: 'Cliente A', status: 'Pendiente', date: '2025-10-06' },
          { id: 'ORD-002', customer: 'Cliente B', status: 'Procesando', date: '2025-10-05' },
          { id: 'ORD-003', customer: 'Cliente C', status: 'Entregado', date: '2025-10-04' }
        ]);
      } catch (error) {
        console.error('Error loading dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Cargando dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Bienvenido, {user?.customerId}</h1>
          <p>Panel de control del sistema de gestión de órdenes</p>
        </div>
        <div className="dashboard-date">
          <span>{new Date().toLocaleDateString('es-ES', { 
            weekday: 'long', 
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
          })}</span>
        </div>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon orders-icon">●</div>
          <div className="stat-content">
            <div className="stat-number">{stats.totalOrders}</div>
            <div className="stat-label">Total Órdenes</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon pending-icon">●</div>
          <div className="stat-content">
            <div className="stat-number">{stats.pendingOrders}</div>
            <div className="stat-label">Pendientes</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon completed-icon">●</div>
          <div className="stat-content">
            <div className="stat-number">{stats.completedOrders}</div>
            <div className="stat-label">Completadas</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon customers-icon">●</div>
          <div className="stat-content">
            <div className="stat-number">{stats.totalCustomers}</div>
            <div className="stat-label">Clientes</div>
          </div>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="dashboard-section">
          <div className="section-header">
            <h2>Órdenes Recientes</h2>
            <button className="btn btn-outline">Ver Todas</button>
          </div>
          <div className="orders-table">
            <table>
              <thead>
                <tr>
                  <th>ID Orden</th>
                  <th>Cliente</th>
                  <th>Estado</th>
                  <th>Fecha</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {recentOrders.map((order) => (
                  <tr key={order.id}>
                    <td>{order.id}</td>
                    <td>{order.customer}</td>
                    <td>
                      <span className={`status-badge ${order.status.toLowerCase()}`}>
                        {order.status}
                      </span>
                    </td>
                    <td>{order.date}</td>
                    <td>
                      <button className="btn btn-sm btn-outline">Ver</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        <div className="dashboard-section">
          <div className="section-header">
            <h2>Acciones Rápidas</h2>
          </div>
          <div className="quick-actions">
            <button className="action-btn primary">
              <span className="action-icon">●</span>
              <span>Nueva Orden</span>
            </button>
            <button className="action-btn secondary">
              <span className="action-icon">●</span>
              <span>Nuevo Cliente</span>
            </button>
            <button className="action-btn tertiary">
              <span className="action-icon">●</span>
              <span>Generar Reporte</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
